"use strict";(globalThis.webpackChunkk_8_s_security=globalThis.webpackChunkk_8_s_security||[]).push([[9104],{6444(e){e.exports=JSON.parse('{"tag":{"label":"CKS","permalink":"/kubernetes-security/tags/cks","allTagsPath":"/kubernetes-security/tags","count":63,"items":[{"id":"best-practices/cluster-setup-and-hardening/rbac-and-identity/audit2rbac","title":"audit2rbac","description":"audit2rbac automatically generates RBAC policies from Kubernetes audit logs, enabling precise least-privilege configurations based on actual API usage.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/rbac-and-identity/audit2rbac"},{"id":"attack-vectors/cloud-metadata-service-abuse","title":"Cloud Metadata Service Abuse","description":"How attackers exploit cloud provider metadata services (IMDS) from Kubernetes pods to steal IAM credentials and escalate privileges.","permalink":"/kubernetes-security/attack-vectors/cloud-metadata-service-abuse"},{"id":"best-practices/cluster-setup-and-hardening/network-security/cloud-metadata-mitigation","title":"Cloud Metadata Service Mitigation","description":"How to prevent pods from accessing cloud provider metadata services (IMDS) and stealing IAM credentials in Kubernetes.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/network-security/cloud-metadata-mitigation"},{"id":"best-practices/cluster-setup-and-hardening/configuration-validation/cnspec","title":"cnspec","description":"cnspec is a cloud-native security scanner from Mondoo that assesses Kubernetes clusters, containers, and infrastructure against security policies and compliance frameworks.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/configuration-validation/cnspec"},{"id":"attack-vectors/compromised-api-server","title":"Compromised API Server","description":"Exploiting Kubernetes API server vulnerabilities and how attackers gain unauthorized access.","permalink":"/kubernetes-security/attack-vectors/compromised-api-server"},{"id":"attack-vectors/compromised-sidecars","title":"Compromised Sidecars","description":"How attackers exploit insecure or malicious sidecar containers to intercept data, escalate privileges, and persist within Kubernetes clusters.","permalink":"/kubernetes-security/attack-vectors/compromised-sidecars"},{"id":"attack-vectors/privileged-container-escape","title":"Container Escape","description":"How an attacker can break out of a container and gain control over the host system in Kubernetes.","permalink":"/kubernetes-security/attack-vectors/privileged-container-escape"},{"id":"best-practices/supply-chain-security/cosign","title":"Cosign","description":"Cosign is a container signing and verification tool used to secure container images and enforce supply chain integrity.","permalink":"/kubernetes-security/best-practices/supply-chain-security/cosign"},{"id":"best-practices/cluster-setup-and-hardening/network-security/ddos-mitigation","title":"DDoS Mitigation in Kubernetes","description":"Learn strategies to mitigate Distributed Denial-of-Service (DDoS) attacks in Kubernetes clusters.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/network-security/ddos-mitigation"},{"id":"best-practices/monitoring-logging-and-runtime-security/threatmapper","title":"Deepfence ThreatMapper","description":"Deepfence ThreatMapper is a runtime vulnerability scanner that discovers threats across Kubernetes clusters, VMs, containers, and serverless environments.","permalink":"/kubernetes-security/best-practices/monitoring-logging-and-runtime-security/threatmapper"},{"id":"attack-vectors/ddos-attacks","title":"Denial of Service (DoS) Attacks","description":"How attackers exploit Kubernetes resources to exhaust system capacity, disrupt workloads, and cause service outages.","permalink":"/kubernetes-security/attack-vectors/ddos-attacks"},{"id":"best-practices/cluster-setup-and-hardening/network-security/dns-security","title":"DNS Security in Kubernetes","description":"Protect CoreDNS from spoofing, cache poisoning, DNS tunneling, and unauthorized modifications to enhance Kubernetes cluster security.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/network-security/dns-security"},{"id":"best-practices/cluster-setup-and-hardening/network-security/egress-control","title":"Egress Control in Kubernetes","description":"Restrict and monitor outbound traffic from Kubernetes workloads to prevent data exfiltration, command-and-control communication, and unauthorized external access.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/network-security/egress-control"},{"id":"attack-vectors/ephemeral-container-abuse","title":"Ephemeral Debug Container Abuse","description":"How attackers abuse kubectl debug and ephemeral containers to inject debugging tools, access process namespaces, and compromise Kubernetes workloads.","permalink":"/kubernetes-security/attack-vectors/ephemeral-container-abuse"},{"id":"attack-vectors/exec-attach-credential-theft","title":"Exec/Attach Credential Theft","description":"Attack scenario demonstrating how kubectl exec and attach commands can be abused to steal credentials and sensitive data from running containers.","permalink":"/kubernetes-security/attack-vectors/exec-attach-credential-theft"},{"id":"attack-vectors/exposed-kubelet-api","title":"Exposed Kubelet API","description":"How an exposed Kubelet API can be exploited to execute commands on nodes and compromise Kubernetes clusters.","permalink":"/kubernetes-security/attack-vectors/exposed-kubelet-api"},{"id":"best-practices/monitoring-logging-and-runtime-security/falco","title":"Falco","description":"Falco is a runtime security tool for Kubernetes that detects abnormal behavior and threats based on system call monitoring and security rules.","permalink":"/kubernetes-security/best-practices/monitoring-logging-and-runtime-security/falco"},{"id":"fundamentals/authentication/issue-user-certificate","title":"How to Issue a Certificate for a Kubernetes User","description":"Step-by-step guide on generating and issuing a certificate for a Kubernetes user, including creating roles and configuring kubeconfig.","permalink":"/kubernetes-security/fundamentals/authentication/issue-user-certificate"},{"id":"attack-vectors/imagepullsecrets-theft","title":"ImagePullSecrets Theft","description":"Attack scenario demonstrating how attackers extract image pull secrets to gain unauthorized access to private container registries.","permalink":"/kubernetes-security/attack-vectors/imagepullsecrets-theft"},{"id":"attack-vectors/insecure-csi-drivers","title":"Insecure CSI Drivers","description":"How attackers exploit insecure Container Storage Interface (CSI) drivers to gain unauthorized access to persistent volumes and sensitive data.","permalink":"/kubernetes-security/attack-vectors/insecure-csi-drivers"},{"id":"attack-vectors/insecure-rbac-permissions","title":"Insecure RBAC Permissions","description":"How overly permissive Kubernetes RBAC configurations enable privilege escalation and full cluster compromise.","permalink":"/kubernetes-security/attack-vectors/insecure-rbac-permissions"},{"id":"attack-vectors/insecure-secrets-management","title":"Insecure Secrets Management","description":"Understanding the risks of insecure secrets management in Kubernetes and how it can lead to sensitive data exposure.","permalink":"/kubernetes-security/attack-vectors/insecure-secrets-management"},{"id":"best-practices/cluster-setup-and-hardening/secrets-management/insecure-secrets-management-mitigation","title":"Insecure Secrets Management Mitigation","description":"Best practices to prevent the exposure of sensitive data in Kubernetes through secure secrets management techniques and external secret stores.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/secrets-management/insecure-secrets-management-mitigation"},{"id":"best-practices/supply-chain-security/kbom","title":"KBOM","description":"KBOM (Kubernetes Bill of Materials) Toolkit generates comprehensive inventories of Kubernetes clusters, including components, images, and configurations.","permalink":"/kubernetes-security/best-practices/supply-chain-security/kbom"},{"id":"best-practices/cluster-setup-and-hardening/pod-security/kube-psp-advisor","title":"kube-psp-advisor","description":"kube-psp-advisor generates Pod Security Policies and Pod Security Standards based on the actual security requirements of running workloads.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/pod-security/kube-psp-advisor"},{"id":"best-practices/cluster-setup-and-hardening/configuration-validation/kube-scan","title":"kube-scan","description":"kube-scan is a Kubernetes risk assessment tool that calculates risk scores for workloads based on their security configurations and potential attack impact.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/configuration-validation/kube-scan"},{"id":"best-practices/cluster-setup-and-hardening/rbac-and-identity/kubectl-bindrole","title":"kubectl-bindrole","description":"kubectl-bindrole finds all Kubernetes roles and cluster roles bound to a specified ServiceAccount, User, or Group, helping audit RBAC configurations.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/rbac-and-identity/kubectl-bindrole"},{"id":"best-practices/monitoring-logging-and-runtime-security/kubectl-dig","title":"kubectl-dig","description":"kubectl-dig provides deep visibility into Kubernetes cluster activity using eBPF-based tracing, enabling real-time analysis of system calls and network traffic.","permalink":"/kubernetes-security/best-practices/monitoring-logging-and-runtime-security/kubectl-dig"},{"id":"best-practices/cluster-setup-and-hardening/configuration-validation/kubectl-kubesec","title":"kubectl-kubesec","description":"kubectl-kubesec is a kubectl plugin that scans Kubernetes resources using kubesec.io to identify security risks and provide hardening recommendations.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/configuration-validation/kubectl-kubesec"},{"id":"best-practices/cluster-setup-and-hardening/rbac-and-identity/kubectl-who-can","title":"kubectl-who-can","description":"kubectl-who-can shows which subjects have RBAC permissions to perform specific actions on Kubernetes resources, helping identify privilege distribution.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/rbac-and-identity/kubectl-who-can"},{"id":"best-practices/supply-chain-security/kubei","title":"Kubei","description":"Kubei is a Kubernetes runtime vulnerability scanner that identifies vulnerabilities in container images across your cluster in real-time.","permalink":"/kubernetes-security/best-practices/supply-chain-security/kubei"},{"id":"attack-vectors/kubelet-anonymous-auth","title":"Kubelet Anonymous Authentication Abuse","description":"How attackers exploit kubelet anonymous authentication to execute commands, read pod logs, and access sensitive data on Kubernetes nodes.","permalink":"/kubernetes-security/attack-vectors/kubelet-anonymous-auth"},{"id":"best-practices/cluster-setup-and-hardening/node-security/kubelet-security","title":"Kubelet Security Overview","description":"Overview of Kubernetes Kubelet security covering authentication, authorization, TLS, resource limits, and hardening best practices.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/node-security/kubelet-security"},{"id":"attack-vectors/intro","title":"Kubernetes Attack Vectors","description":"Comprehensive guide to Kubernetes attack vectors including container escapes, privilege escalation, RBAC exploitation, and cluster compromise techniques.","permalink":"/kubernetes-security/attack-vectors/intro"},{"id":"best-practices/cluster-setup-and-hardening/secrets-management/kubernetes-external-secrets","title":"Kubernetes External Secrets","description":"Kubernetes External Secrets Operator synchronizes secrets from external providers like AWS Secrets Manager, HashiCorp Vault, and Azure Key Vault into Kubernetes.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/secrets-management/kubernetes-external-secrets"},{"id":"best-practices/cluster-setup-and-hardening/rbac-and-identity/kubernetes-rbac-audit","title":"kubernetes-rbac-audit","description":"kubernetes-rbac-audit is an auditing tool that analyzes RBAC configurations to identify risky permissions and potential security misconfigurations.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/rbac-and-identity/kubernetes-rbac-audit"},{"id":"attack-vectors/lack-of-network-policies","title":"Lack of Network Policies","description":"How missing Kubernetes Network Policies enable lateral movement and data exfiltration by attackers.","permalink":"/kubernetes-security/attack-vectors/lack-of-network-policies"},{"id":"attack-vectors/misconfigured-admission-controllers","title":"Misconfigured Admission Controllers","description":"How attackers exploit misconfigured Kubernetes admission controllers and insecure webhooks to bypass security policies.","permalink":"/kubernetes-security/attack-vectors/misconfigured-admission-controllers"},{"id":"best-practices/cluster-setup-and-hardening/network-security/netchecks","title":"netchecks","description":"netchecks validates network connectivity assumptions in Kubernetes clusters by running declarative network tests to verify policies and connectivity.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/network-security/netchecks"},{"id":"best-practices/cluster-setup-and-hardening/network-security/network-policies","title":"Network Policies","description":"Explore how Network Policies in Kubernetes control traffic flow and enhance security.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/network-security/network-policies"},{"id":"attack-vectors/persistent-volume-data-exposure","title":"Persistent Volume Data Exposure","description":"How attackers exploit misconfigured PersistentVolumes to access sensitive data from other workloads or previously deleted pods in Kubernetes.","permalink":"/kubernetes-security/attack-vectors/persistent-volume-data-exposure"},{"id":"best-practices/cluster-setup-and-hardening/pod-security/pod-security-standards","title":"Pod Security Standards (PSS)","description":"Learn how Kubernetes Pod Security Standards (PSS) enforce security controls for workloads and replace the deprecated Pod Security Policies (PSP).","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/pod-security/pod-security-standards"},{"id":"attack-vectors/privileged-service-accounts","title":"Privileged Service Accounts","description":"How attackers exploit overly privileged Kubernetes Service Accounts to gain cluster-wide access and escalate privileges.","permalink":"/kubernetes-security/attack-vectors/privileged-service-accounts"},{"id":"best-practices/cluster-setup-and-hardening/rbac-and-identity/rakkess","title":"rakkess","description":"rakkess displays an access matrix showing which Kubernetes resources a user, group, or service account can access, providing a comprehensive RBAC overview.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/rbac-and-identity/rakkess"},{"id":"best-practices/cluster-setup-and-hardening/rbac-and-identity/rback","title":"rback","description":"rback generates visual diagrams of Kubernetes RBAC configurations, making it easier to understand and audit complex permission structures.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/rbac-and-identity/rback"},{"id":"fundamentals/authorization/rbac","title":"Role-Based Access Control (RBAC)","description":"Learn how Role-Based Access Control (RBAC) in Kubernetes manages authorization and improves security.","permalink":"/kubernetes-security/fundamentals/authorization/rbac"},{"id":"best-practices/cluster-setup-and-hardening/rbac-and-identity/debug-container-security","title":"Securing Debug Container Access","description":"How to control ephemeral container and kubectl debug access through RBAC, Pod Security Standards, and admission control in Kubernetes.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/rbac-and-identity/debug-container-security"},{"id":"best-practices/cluster-setup-and-hardening/node-security/kubelet-authentication","title":"Securing Kubelet Authentication and Authorization","description":"How to disable kubelet anonymous authentication, configure certificate-based authentication, and implement proper authorization to protect Kubernetes nodes.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/node-security/kubelet-authentication"},{"id":"best-practices/cluster-setup-and-hardening/pod-security/persistent-volume-security","title":"Securing Persistent Volumes","description":"How to protect Kubernetes PersistentVolumes from unauthorized access, data exposure, and cross-namespace attacks through proper configuration and RBAC.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/pod-security/persistent-volume-security"},{"id":"best-practices/cluster-setup-and-hardening/api-server-security/compromised-api-server-mitigation","title":"Securing the Kubernetes API Server","description":"Best practices for protecting the Kubernetes API server against unauthorized access and exploitation.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/api-server-security/compromised-api-server-mitigation"},{"id":"attack-vectors/exposed-dashboard","title":"Security Risks of Exposing Kubernetes Dashboard Publicly","description":"Learn the security risks of exposing Kubernetes Dashboard publicly and how attackers exploit misconfigured dashboards for full cluster compromise.","permalink":"/kubernetes-security/attack-vectors/exposed-dashboard"},{"id":"attack-vectors/service-account-token-abuse","title":"Service Account Token Projection Abuse","description":"Attack scenario demonstrating exploitation of service account tokens with excessive permissions or long lifetimes.","permalink":"/kubernetes-security/attack-vectors/service-account-token-abuse"},{"id":"fundamentals/authentication/service-accounts","title":"Service Accounts","description":"Learn how Kubernetes Service Accounts provide authentication for pods and how to securely configure them using RBAC.","permalink":"/kubernetes-security/fundamentals/authentication/service-accounts"},{"id":"best-practices/cluster-setup-and-hardening/network-security/service-mesh-security","title":"Service Mesh Security","description":"Improve Kubernetes security by implementing mutual TLS (mTLS), zero-trust networking, and policy-based access control using service meshes like Istio, Linkerd, and Cilium.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/network-security/service-mesh-security"},{"id":"best-practices/cluster-setup-and-hardening/configuration-validation/steampipe","title":"Steampipe for Kubernetes","description":"Steampipe enables SQL-based querying of Kubernetes resources and compliance scanning using the steampipe-kubernetes plugin and compliance mod.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/configuration-validation/steampipe"},{"id":"attack-vectors/supply-chain-attacks","title":"Supply Chain Attacks","description":"How attackers compromise container images, dependencies, CI/CD pipelines, and Helm charts to infiltrate Kubernetes clusters.","permalink":"/kubernetes-security/attack-vectors/supply-chain-attacks"},{"id":"attack-vectors/traffic-hijacking","title":"Traffic Hijacking","description":"How attackers manipulate Kubernetes network traffic to intercept, redirect, or disrupt communication between workloads.","permalink":"/kubernetes-security/attack-vectors/traffic-hijacking"},{"id":"best-practices/monitoring-logging-and-runtime-security/trivy","title":"Trivy","description":"Overview, usage, and best practices for using Trivy to scan container images, file systems, and Kubernetes resources for vulnerabilities.","permalink":"/kubernetes-security/best-practices/monitoring-logging-and-runtime-security/trivy"},{"id":"best-practices/supply-chain-security/trivy-operator","title":"Trivy Operator","description":"Trivy Operator provides Kubernetes-native security scanning by automatically scanning workloads for vulnerabilities, misconfigurations, secrets, and RBAC issues.","permalink":"/kubernetes-security/best-practices/supply-chain-security/trivy-operator"},{"id":"fundamentals/understanding-k8s-attack-surface","title":"Understanding the Kubernetes Attack Surface","description":"An overview of potential attack vectors in Kubernetes and strategies to mitigate security risks.","permalink":"/kubernetes-security/fundamentals/understanding-k8s-attack-surface"},{"id":"attack-vectors/unrestricted-etcd-access","title":"Unrestricted etcd Access","description":"How attackers exploit unrestricted access to etcd to retrieve Kubernetes secrets and take control of the cluster.","permalink":"/kubernetes-security/attack-vectors/unrestricted-etcd-access"},{"id":"attack-vectors/unrestricted-hostpath-mounts","title":"Unrestricted HostPath Mounts","description":"How attackers exploit unrestricted hostPath mounts to gain access to the host filesystem and escalate privileges.","permalink":"/kubernetes-security/attack-vectors/unrestricted-hostpath-mounts"},{"id":"best-practices/cluster-setup-and-hardening/secrets-management/vault-secrets-operator","title":"Vault Secrets Operator","description":"Vault Secrets Operator is HashiCorp\'s official Kubernetes operator for synchronizing secrets from Vault into Kubernetes Secrets.","permalink":"/kubernetes-security/best-practices/cluster-setup-and-hardening/secrets-management/vault-secrets-operator"}],"unlisted":false}}')}}]);