"use strict";(globalThis.webpackChunkk_8_s_security=globalThis.webpackChunkk_8_s_security||[]).push([[5753],{9639(e){e.exports=JSON.parse('{"tag":{"label":"tool","permalink":"/docs/tags/tool","allTagsPath":"/docs/tags","count":28,"items":[{"id":"best_practices/cluster_setup_and_hardening/rbac_and_identity/aad_pod_identity","title":"aad-pod-identity","description":"aad-pod-identity enables Kubernetes pods on Azure to use Azure Active Directory identities for accessing Azure resources without storing credentials.","permalink":"/docs/best_practices/cluster_setup_and_hardening/rbac_and_identity/aad_pod_identity"},{"id":"best_practices/cluster_setup_and_hardening/rbac_and_identity/audit2rbac","title":"audit2rbac","description":"audit2rbac automatically generates RBAC policies from Kubernetes audit logs, enabling precise least-privilege configurations based on actual API usage.","permalink":"/docs/best_practices/cluster_setup_and_hardening/rbac_and_identity/audit2rbac"},{"id":"tools/cdk","title":"CDK (Container Penetration Toolkit)","description":"CDK is a zero-dependency container penetration toolkit for assessing container security, discovering escape vectors, and testing Kubernetes defenses.","permalink":"/docs/tools/cdk"},{"id":"best_practices/cluster_setup_and_hardening/configuration_validation/cnspec","title":"cnspec","description":"cnspec is a cloud-native security scanner from Mondoo that assesses Kubernetes clusters, containers, and infrastructure against security policies and compliance frameworks.","permalink":"/docs/best_practices/cluster_setup_and_hardening/configuration_validation/cnspec"},{"id":"best_practices/supply_chain_security/cosign","title":"Cosign","description":"Cosign is a container signing and verification tool used to secure container images and enforce supply chain integrity.","permalink":"/docs/best_practices/supply_chain_security/cosign"},{"id":"best_practices/monitoring_logging_and_runtime_security/threatmapper","title":"Deepfence ThreatMapper","description":"Deepfence ThreatMapper is a runtime vulnerability scanner that discovers threats across Kubernetes clusters, VMs, containers, and serverless environments.","permalink":"/docs/best_practices/monitoring_logging_and_runtime_security/threatmapper"},{"id":"best_practices/monitoring_logging_and_runtime_security/falco","title":"Falco","description":"Falco is a runtime security tool for Kubernetes that detects abnormal behavior and threats based on system call monitoring and security rules.","permalink":"/docs/best_practices/monitoring_logging_and_runtime_security/falco"},{"id":"best_practices/supply_chain_security/kbom","title":"KBOM","description":"KBOM (Kubernetes Bill of Materials) Toolkit generates comprehensive inventories of Kubernetes clusters, including components, images, and configurations.","permalink":"/docs/best_practices/supply_chain_security/kbom"},{"id":"tools/kdigger","title":"kdigger","description":"kdigger is a Kubernetes-focused container assessment and context discovery tool for penetration testing and security assessments.","permalink":"/docs/tools/kdigger"},{"id":"best_practices/cluster_setup_and_hardening/rbac_and_identity/kiam","title":"kiam","description":"kiam provides AWS IAM credentials to pods running on Kubernetes, using a client-server architecture for improved security over metadata interception.","permalink":"/docs/best_practices/cluster_setup_and_hardening/rbac_and_identity/kiam"},{"id":"best_practices/cluster_setup_and_hardening/pod_security/kube_psp_advisor","title":"kube-psp-advisor","description":"kube-psp-advisor generates Pod Security Policies and Pod Security Standards based on the actual security requirements of running workloads.","permalink":"/docs/best_practices/cluster_setup_and_hardening/pod_security/kube_psp_advisor"},{"id":"best_practices/cluster_setup_and_hardening/configuration_validation/kube_scan","title":"kube-scan","description":"kube-scan is a Kubernetes risk assessment tool that calculates risk scores for workloads based on their security configurations and potential attack impact.","permalink":"/docs/best_practices/cluster_setup_and_hardening/configuration_validation/kube_scan"},{"id":"best_practices/cluster_setup_and_hardening/rbac_and_identity/kube2iam","title":"kube2iam","description":"kube2iam enables Kubernetes pods to assume AWS IAM roles, providing fine-grained AWS credential management without exposing long-lived credentials.","permalink":"/docs/best_practices/cluster_setup_and_hardening/rbac_and_identity/kube2iam"},{"id":"best_practices/cluster_setup_and_hardening/rbac_and_identity/kubectl_bindrole","title":"kubectl-bindrole","description":"kubectl-bindrole finds all Kubernetes roles and cluster roles bound to a specified ServiceAccount, User, or Group, helping audit RBAC configurations.","permalink":"/docs/best_practices/cluster_setup_and_hardening/rbac_and_identity/kubectl_bindrole"},{"id":"best_practices/monitoring_logging_and_runtime_security/kubectl_dig","title":"kubectl-dig","description":"kubectl-dig provides deep visibility into Kubernetes cluster activity using eBPF-based tracing, enabling real-time analysis of system calls and network traffic.","permalink":"/docs/best_practices/monitoring_logging_and_runtime_security/kubectl_dig"},{"id":"best_practices/cluster_setup_and_hardening/configuration_validation/kubectl_kubesec","title":"kubectl-kubesec","description":"kubectl-kubesec is a kubectl plugin that scans Kubernetes resources using kubesec.io to identify security risks and provide hardening recommendations.","permalink":"/docs/best_practices/cluster_setup_and_hardening/configuration_validation/kubectl_kubesec"},{"id":"best_practices/cluster_setup_and_hardening/rbac_and_identity/kubectl_who_can","title":"kubectl-who-can","description":"kubectl-who-can shows which subjects have RBAC permissions to perform specific actions on Kubernetes resources, helping identify privilege distribution.","permalink":"/docs/best_practices/cluster_setup_and_hardening/rbac_and_identity/kubectl_who_can"},{"id":"best_practices/supply_chain_security/kubei","title":"Kubei","description":"Kubei is a Kubernetes runtime vulnerability scanner that identifies vulnerabilities in container images across your cluster in real-time.","permalink":"/docs/best_practices/supply_chain_security/kubei"},{"id":"best_practices/cluster_setup_and_hardening/secrets_management/kubernetes_external_secrets","title":"Kubernetes External Secrets","description":"Kubernetes External Secrets Operator synchronizes secrets from external providers like AWS Secrets Manager, HashiCorp Vault, and Azure Key Vault into Kubernetes.","permalink":"/docs/best_practices/cluster_setup_and_hardening/secrets_management/kubernetes_external_secrets"},{"id":"best_practices/cluster_setup_and_hardening/rbac_and_identity/kubernetes_rbac_audit","title":"kubernetes-rbac-audit","description":"kubernetes-rbac-audit is an auditing tool that analyzes RBAC configurations to identify risky permissions and potential security misconfigurations.","permalink":"/docs/best_practices/cluster_setup_and_hardening/rbac_and_identity/kubernetes_rbac_audit"},{"id":"best_practices/cluster_setup_and_hardening/network_security/netchecks","title":"netchecks","description":"netchecks validates network connectivity assumptions in Kubernetes clusters by running declarative network tests to verify policies and connectivity.","permalink":"/docs/best_practices/cluster_setup_and_hardening/network_security/netchecks"},{"id":"best_practices/cluster_setup_and_hardening/rbac_and_identity/rakkess","title":"rakkess","description":"rakkess displays an access matrix showing which Kubernetes resources a user, group, or service account can access, providing a comprehensive RBAC overview.","permalink":"/docs/best_practices/cluster_setup_and_hardening/rbac_and_identity/rakkess"},{"id":"best_practices/cluster_setup_and_hardening/rbac_and_identity/rback","title":"rback","description":"rback generates visual diagrams of Kubernetes RBAC configurations, making it easier to understand and audit complex permission structures.","permalink":"/docs/best_practices/cluster_setup_and_hardening/rbac_and_identity/rback"},{"id":"tools/red_kube","title":"red-kube","description":"red-kube is a Kubernetes adversary emulation framework based on kubectl, providing attack simulations aligned with MITRE ATT&CK tactics.","permalink":"/docs/tools/red_kube"},{"id":"best_practices/cluster_setup_and_hardening/configuration_validation/steampipe","title":"Steampipe for Kubernetes","description":"Steampipe enables SQL-based querying of Kubernetes resources and compliance scanning using the steampipe-kubernetes plugin and compliance mod.","permalink":"/docs/best_practices/cluster_setup_and_hardening/configuration_validation/steampipe"},{"id":"best_practices/monitoring_logging_and_runtime_security/trivy","title":"Trivy","description":"Overview, usage, and best practices for using Trivy to scan container images, file systems, and Kubernetes resources for vulnerabilities.","permalink":"/docs/best_practices/monitoring_logging_and_runtime_security/trivy"},{"id":"best_practices/supply_chain_security/trivy_operator","title":"Trivy Operator","description":"Trivy Operator provides Kubernetes-native security scanning by automatically scanning workloads for vulnerabilities, misconfigurations, secrets, and RBAC issues.","permalink":"/docs/best_practices/supply_chain_security/trivy_operator"},{"id":"best_practices/cluster_setup_and_hardening/secrets_management/vault_secrets_operator","title":"Vault Secrets Operator","description":"Vault Secrets Operator is HashiCorp\'s official Kubernetes operator for synchronizing secrets from Vault into Kubernetes Secrets.","permalink":"/docs/best_practices/cluster_setup_and_hardening/secrets_management/vault_secrets_operator"}],"unlisted":false}}')}}]);